import { legacy_createStore as createStore } from 'redux';
import authReducer from './authReducer';

  
const configureStore = () => {

  const localAuthData = localStorage.getItem("auth");

  let stateInLocalStorage = {
    isLoggedIn: false,
    username: null,
    displayName: null,
    image: null,
    password: null
  }

  if(localAuthData){
    stateInLocalStorage = JSON.parse(localAuthData);
  }

  const store = createStore(authReducer, stateInLocalStorage,window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__());  

  // each time the state changes, this method will run
  store.subscribe(() => {
    localStorage.setItem("auth",JSON.stringify(store.getState()))
  })

  return store; 
}

export default configureStore;
