import React from "react";
import Input from "../components/input"
import { withTranslation } from "react-i18next"
import { onChangeLanguage } from "../components/LanguageSelector";

class UserLoginPage extends React.Component{

    state = {
        username: null,
        password: null
    };

    onChange = (event) =>{
        const {name,value} = event.target;
        this.setState({
            [name]: value
        })

    }

    render(){
        const { t } = this.props
        const {username,password} = this.state
        return(
            <div className="container">
                <form>
                    <h1>{t("Login")}</h1>
                    <Input type="text" name="username" label={t("Username")} onChange={this.onChange}/>
                    <Input type="password" name="password" label={t("Password")} onChange={this.onChange}/>
                    <button className="btn btn-primary" disabled={!username || !password}>{t("Login")}</button>
                </form>
                <div>
                <img
                    src="https://flagcdn.com/h24/tr.png"
                    srcset="https://flagcdn.com/h48/tr.png 2x"
                    height="24"
                    alt="Turkey"
                    onClick={() => onChangeLanguage("tr")}
                    style={{cursor: "pointer"}}
                    />
                    <img
                        src="https://flagcdn.com/h24/us.png"
                        srcset="https://flagcdn.com/h48/us.png 2x"
                        height="24"
                        alt="United States" onClick={ () => onChangeLanguage("en")}
                        style={{cursor:"pointer"}}
                        />
                </div>
            </div>
            
        )
    }
}

export default withTranslation()(UserLoginPage);
