import logo from "../assets/hoaxify.png";
import { Link } from 'react-router-dom/cjs/react-router-dom.min';
import { useTranslation} from 'react-i18next';
//import { Authentication } from '../shared/AuthenticationContext';
import { useDispatch, useSelector } from 'react-redux';
import { logoutSuccess } from '../redux/authActions';
import { clearAuthorizationHeader } from "../api/apiCalls";
import DropdownMenu from "./DropdownMenu";

const TopBar = (props) => {

    const {t} = useTranslation();
    const dispatch = useDispatch();
    const { isLoggedIn, username} = useSelector((store) => {
        return {
            isLoggedIn: store.isLoggedIn,
            username: store.username
        };
    });

    const onClickLogout = () => {
        dispatch(logoutSuccess())
        clearAuthorizationHeader();
    }

    //const cannot be reassigned once assigned
    //we should use let
    let links = (
        <ul className='navbar-nav ms-auto'>
        <li className='text-white'>
            <Link className="nav-link" to="/login">
            {t("Login")}
            </Link>
        </li>
        <li className='text-white'>
            <Link className="nav-link me-5" to="/signup">
            {t("Sign up")}
            </Link>
        </li>
    </ul>
    );

    if (isLoggedIn) {
        links = (
        <ul className='navbar-nav ms-auto'>
            <li className="nav-item dropdown">
            <span className='nav-link text-white dropdown-toggle me-5'> My profile</span>
                <div className="dropdown-menu">
                <Link className="dropdown-item" to={"/user/" + username}>
                {t("My Profile")}
                </Link>
                <Link className="dropdown-item" to="/login">
                {t("Logout")}
                </Link>
                </div>
            </li>
        </ul>
        )
    }

    return (
        <div className='bg-light mb-2'>
            <nav className="navbar navbar-dark navbar-expand bg-primary shadow">
                <Link className="navbar-brand" to="/">
                    <img src={logo} width="60"/>
                    Hoaxify
                </Link>

                {links}
            </nav>
        </div>
    )
}


export default TopBar;
