import React, { useState } from 'react';
import { useSelector } from 'react-redux';
import ProfileImageWithDefault from './ProfileImageWithDefault';

const HoaxifySection = (props) => {

    const {image} = useSelector((store) => {
        return {
            image: store.image
        }
    })

    const [showButtons,setShowButtons] = useState(false);

    const onClickTextArea = () => {
        setShowButtons(!showButtons);
    }

    return (
        <div className='card'>
            <div className='row'>
                <div className='col-auto'>
                <ProfileImageWithDefault image={image} width="32" height="32" />
                </div>
                <div className='col'>
                    <form>
                        <div class="form-group">
                            <textarea class="form-control" rows="5" onClick={onClickTextArea}></textarea>
                        </div>
                        <button className='btn btn-primary mt-2 ms-10'>Hoaxify</button>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default HoaxifySection;
