import React from "react";
import UserSignupPage from "../pages/UserSignupPage";
import UserLoginPage from "../pages/UserLoginPage";
import HomePage from "../pages/HomePage";
import LanguageSelector from "../components/LanguageSelector";
import UserPage from "../pages/UserPage";
import TopBar from "../components/TopBar";
import { HashRouter,Switch,Route,Redirect } from "react-router-dom/cjs/react-router-dom.min";

class App extends React.Component {

  state = {
    isLoggedIn: true,
    username: "user"
  }

  onLoginSuccess = (username) => {
    this.setState({
      isLoggedIn: true,
      username: username
    })
  }

  render(){
    const { isLoggedIn, username } = this.state;
    return (
      <div>
        <HashRouter>
          <TopBar username={username} isLoggedIn={isLoggedIn} />
          <Switch>
            <Route exact path="/" component={HomePage}/>
            <Route path="/login" component={function(reactRouterProps){
              return <UserLoginPage {...reactRouterProps}/>;
            }}/>
            <Route path="/signup" component={UserSignupPage}/>
            <Route path="/user/:username" component={UserPage}/>
            <Redirect to="/" />
          </Switch>
        </HashRouter>
        <LanguageSelector />
      </div>
      );

  }

}

export default App;
