import axios from "axios"

export const signup = (body) => {
    //then will be launched if the request is successfull
    //catch will be launched if the request is failed
    axios.post("/api/1.0/users",body).then(
        (response) => {
            this.setState({
                pendingApiCall: false
            })
        }
    ).catch((error) => {
        this.setState({
            pendingApiCall: false
        })
    })
}
