import i18n from "i18next";
import {initReactI18next} from "react-i18next"

i18n.use(initReactI18next).init({
    resources: {
        en: {
            translations: {
                "Sign Up" : "Sign Up"
            }
        },
        tr: {
            translations: {

            }
        }
    },
    fallbackLng: "en",
    ns: ["translations"],
    defaultNS: "translations",
    keySeperator: false,
    interpolation: {
        escapeValue: false,
        formatSeperator: ","
    },
    react: {
        wait: true
    }
});

export default i18n;
