import React from "react";

const Input = (props) => {
    const className = props.error == null ? "form-control" : "form-control is-invalid"
    return (
        <div className="form-group">
        <label>{props.label}</label>
        <input type="text" className={className} name={props.name} onChange={props.onChange}/>
        <div className="invalid-feedback">
            {props.error}
        </div>
    </div>
    )
};

export default Input;
