import React, { useEffect, useState } from 'react';
import { getHoaxes } from '../api/apiCalls';
import HoaxListItem from './HoaxListItem';

const HoaxFeed = () => {

    const [page,setPage] = useState({
        content: [],
        size: 5,
        number: 0,
        totalPages: 0
    });


    useEffect(() => {
        loadHoaxes();
    },[]);

    const loadHoaxes = async (pageNumber) => {
        const response = await getHoaxes(pageNumber);
        setPage(response.data);    
    }

    const onClickLoadHoaxes = () => {
        const nextPage = page.number + 1;
        loadHoaxes(nextPage);
    }

    const {content: hoaxes} = page;



    return (
        <div>
            {hoaxes.map((value,index) => {
                const {content: hoaxContent, timeStamp} = value;
                return (
                    <HoaxListItem content={hoaxContent} timeStamp={timeStamp} />
                )
            })}
            <div className='text-center mt-2'>
                <button className='btn btn-primary' onClick={onClickLoadHoaxes}>Load Hoaxes</button>
            </div>
        </div>
    );
};

export default HoaxFeed;
